package com.afsal.dev.typicodapp.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.afsal.dev.typicodapp.databinding.AlbumItemBindingimport com.afsal.dev.typicodapp.databinding.PostItemBindingclass AlbumAdapter():RecyclerView.Adapter<AlbumAdapter.ItemHolder>() {    val list= listOf("post1","post1","post1","post1","post1","post1","post1","post1","post1",)    class ItemHolder(val binding: AlbumItemBinding ):RecyclerView.ViewHolder(binding.root) {    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ItemHolder {        return ItemHolder(AlbumItemBinding.inflate(LayoutInflater.from(parent.context),parent,false))    }    override fun onBindViewHolder(holder: ItemHolder, position: Int) {        val item=list[position]        holder.binding.albumText.text=item    }    override fun getItemCount()=list.size}