package com.afsal.dev.typicodapp.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.AsyncListDifferimport androidx.recyclerview.widget.DiffUtilimport androidx.recyclerview.widget.RecyclerViewimport com.afsal.dev.typicodapp.databinding.AlbumLayoutItemBindingimport com.afsal.dev.typicodapp.model.AlbumsItemclass AlbumAdapter(private val onItemClicked: (albumId: Int) -> Unit) :    RecyclerView.Adapter<AlbumAdapter.ItemHolder>() {    val list =        listOf("post1", "post1", "post1", "post1", "post1", "post1", "post1", "post1", "post1")    private val diffCallBack = object : DiffUtil.ItemCallback<AlbumsItem>() {        override fun areItemsTheSame(oldItem: AlbumsItem, newItem: AlbumsItem): Boolean {            return oldItem.id == newItem.id        }        override fun areContentsTheSame(oldItem: AlbumsItem, newItem: AlbumsItem): Boolean {            return oldItem == newItem        }    }    val differ = AsyncListDiffer(this, diffCallBack)    inner class ItemHolder(val binding:AlbumLayoutItemBinding) : RecyclerView.ViewHolder(binding.root) {        init {            binding.cardLayout.setOnClickListener {                onItemClicked(differ.currentList[adapterPosition].id)            }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ItemHolder {        return ItemHolder(            AlbumLayoutItemBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun onBindViewHolder(holder: ItemHolder, position: Int) {        holder.binding.albumText.text = differ.currentList[position].title    }    override fun getItemCount() =differ.currentList.size}