package com.afsal.dev.typicodapp.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.AsyncListDifferimport androidx.recyclerview.widget.DiffUtilimport androidx.recyclerview.widget.RecyclerViewimport com.afsal.dev.typicodapp.databinding.CommentsItemBindingimport com.afsal.dev.typicodapp.model.CommentsItemclass CommentsAdapter() :    RecyclerView.Adapter<CommentsAdapter.ItemHolder>() {    private val diffCallBack = object : DiffUtil.ItemCallback<CommentsItem>() {        override fun areItemsTheSame(oldItem: CommentsItem, newItem: CommentsItem): Boolean {            return oldItem.id == newItem.id        }        override fun areContentsTheSame(oldItem: CommentsItem, newItem: CommentsItem): Boolean {            return oldItem == newItem        }    }    val differ = AsyncListDiffer(this, diffCallBack)    inner class ItemHolder(val binding: CommentsItemBinding) : RecyclerView.ViewHolder(binding.root) {        fun bind(item: CommentsItem) {            binding.apply {              nameText.text=item.name                commentText.text=item.body            }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ItemHolder {        return ItemHolder(            CommentsItemBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun onBindViewHolder(holder: ItemHolder, position: Int) {        val item = differ.currentList[position]        holder.bind(item)    }    override fun getItemCount() = differ.currentList.size}