package com.afsal.dev.typicodapp.adaptersimport android.util.Logimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.AsyncListDifferimport androidx.recyclerview.widget.DiffUtilimport androidx.recyclerview.widget.RecyclerViewimport com.afsal.dev.typicodapp.Rimport com.afsal.dev.typicodapp.databinding.AlbumLayoutItemBindingimport com.afsal.dev.typicodapp.databinding.PhotosItemLayoutBindingimport com.afsal.dev.typicodapp.model.AlbumsItemimport com.afsal.dev.typicodapp.model.PhotosItemimport com.afsal.dev.typicodapp.uttil.loadImageclass PhotosAdapter() :    RecyclerView.Adapter<PhotosAdapter.ItemHolder>() {    private val diffCallBack = object : DiffUtil.ItemCallback<PhotosItem>() {        override fun areItemsTheSame(oldItem: PhotosItem, newItem: PhotosItem): Boolean {            return oldItem.id == newItem.id        }        override fun areContentsTheSame(oldItem: PhotosItem, newItem: PhotosItem): Boolean {            return oldItem == newItem        }    }    val differ = AsyncListDiffer(this, diffCallBack)    inner class ItemHolder(val binding:PhotosItemLayoutBinding) : RecyclerView.ViewHolder(binding.root) {        fun bind(photo:PhotosItem){            binding.title.text=photo.title           loadImage(photo.thumbnailUrl,binding.imageView)            Log.d("DDD","iamgeUrl ${photo.thumbnailUrl}")        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ItemHolder {        return ItemHolder(            PhotosItemLayoutBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun onBindViewHolder(holder: ItemHolder, position: Int) {     val item=differ.currentList[position]        holder.bind(item)    }    override fun getItemCount() =   differ.currentList.size}