package com.afsal.dev.typicodapp.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.afsal.dev.typicodapp.databinding.PostItemBindingclass PostesAdapter():RecyclerView.Adapter<PostesAdapter.ItemHolder>() {    val list= listOf("post1","post1","post1","post1","post1","post1","post1","post1","post1",)    class ItemHolder(val binding: PostItemBinding):RecyclerView.ViewHolder(binding.root) {    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ItemHolder {        return ItemHolder(PostItemBinding.inflate(LayoutInflater.from(parent.context),parent,false))    }    override fun onBindViewHolder(holder: ItemHolder, position: Int) {        val item=list[position]        holder.binding.title.text=item    }    override fun getItemCount()=list.size}