package com.afsal.dev.typicodapp.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.AsyncListDifferimport androidx.recyclerview.widget.DiffUtilimport androidx.recyclerview.widget.RecyclerViewimport com.afsal.dev.typicodapp.databinding.PostItemBindingimport com.afsal.dev.typicodapp.model.PostsModelclass PostesAdapter(private val onCommentClicked: (commentId: Int) -> Unit) :    RecyclerView.Adapter<PostesAdapter.ItemHolder>() {    private val diffCallBack = object : DiffUtil.ItemCallback<PostsModel>() {        override fun areItemsTheSame(oldItem: PostsModel, newItem: PostsModel): Boolean {            return oldItem.id == newItem.id        }        override fun areContentsTheSame(oldItem: PostsModel, newItem: PostsModel): Boolean {            return oldItem == newItem        }    }    val differ = AsyncListDiffer(this, diffCallBack)    inner class ItemHolder(val binding: PostItemBinding) : RecyclerView.ViewHolder(binding.root) {        init {            binding.commentBt.setOnClickListener {                val id = differ.currentList[adapterPosition].id                onCommentClicked(id)            }        }        fun bind(post: PostsModel) {            binding.apply {                title.text = post.title                text.text = post.body            }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ItemHolder {        return ItemHolder(            PostItemBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun onBindViewHolder(holder: ItemHolder, position: Int) {        val item = differ.currentList[position]        holder.bind(item)    }    override fun getItemCount() = differ.currentList.size}