package com.afsal.dev.typicodapp.netWorkimport com.afsal.dev.typicodapp.helper.Constents.ALBUMSimport com.afsal.dev.typicodapp.helper.Constents.PHOTOSimport com.afsal.dev.typicodapp.helper.Constents.POSTSimport com.afsal.dev.typicodapp.model.*import retrofit2.Responseimport retrofit2.http.GETimport retrofit2.http.Pathinterface ApiService {@GET(POSTS)suspend fun getAllPosts(): Response<List<PostsModel>>@GET("/posts/{id}/comments" )suspend fun getCommentOnPost(@Path("id")id:Int):Response<List<CommentsItem>>@GET(ALBUMS)suspend fun getAlbums():Response<List<AlbumsItem>>@GET("/album/{id}/photos")suspend fun getPhotosOnAlbum(@Path("id") id:Int):Response<Photos>}