package com.afsal.dev.typicodapp.uttilimport android.view.Viewimport androidx.fragment.app.Fragmentimport com.afsal.dev.typicodapp.netWork.Resourceimport com.google.android.material.snackbar.Snackbarfun View.showSnackBar(message:String,action:(()->Unit)?=null){    val snackbar=Snackbar.make(this,message,Snackbar.LENGTH_LONG)    action?.let {        snackbar.setAction("Retry"){            it()        }    }    snackbar.show()}fun Fragment.handleApiError(failure:Resource.Failure,retry:(()->Unit)?= null ){    when{        failure.isNetworkError ==true ->requireView().showSnackBar("Pleas check your Internet connection !!",retry)       else ->{           val error= failure.errorBody?.toString().toString()           requireView().showSnackBar(error)       }    }}